import React, { useState } from 'react';
import { api } from '../api';
import { loadStripe } from '@stripe/stripe-js';
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_KEY);

export default function PhoneForm({ setPhone, data, setData }) {
  const [input, setInput] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    setPhone(input);
    const res = await api.get(`/sms/${input}`);
    if (res.data.exists) {
      setData(res.data);
    } else {
      // initiate Stripe checkout
      const stripe = await stripePromise;
      const session = await api.post('/payment/create-checkout-session', { phone: input });
      await stripe.redirectToCheckout({ sessionId: session.data.id });
    }
  };

  return (
    <form onSubmit={handleSubmit} className="flex space-x-2">
      <input
        className="border p-2 rounded"
        placeholder="Enter phone number"
        value={input}
        onChange={e => setInput(e.target.value)}
      />
      <button className="px-4 py-2 bg-blue-500 text-white rounded">Go</button>
    </form>
  );
}
